import React, { useState } from 'react';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

import Cart from './components/Cart';
import Checkout from './components/Checkout';
import Admin from './components/Admin';

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [discountCodes, setDiscountCodes] = useState([]);
  const [purchaseInfo, setPurchaseInfo] = useState({
    itemsCount: 0,
    totalPurchaseAmount: 0,
    discountCodes: [],
    totalDiscountAmount: 0,
  });
  const [orderCounter, setOrderCounter] = useState(0);

  const addItemToCart = (item) => {
    setCartItems([...cartItems, item]);

    // Update purchase information when an item is added to the cart
    setPurchaseInfo({
      ...purchaseInfo,
      itemsCount: purchaseInfo.itemsCount + 1,
      totalPurchaseAmount: purchaseInfo.totalPurchaseAmount + item.price,
    });
  };

  const handleCheckout = (code) => {
    // Increment the order counter
    setOrderCounter(orderCounter + 1);

    // Check if it's an nth order
    const N = 3; // Example: Apply discount on every 3rd order
    if (orderCounter % N === 0) {
      applyDiscountCode(code);
    } else {
      alert('Order placed successfully');
    }
  };

  const applyDiscountCode = (code) => {
    // Simulate a valid discount code
    const validDiscountCode = 'DISCOUNT10';

    if (code === validDiscountCode) {
      const discountAmount = calculateDiscount(cartItems);
      setPurchaseInfo({
        ...purchaseInfo,
        discountCodes: [...purchaseInfo.discountCodes, code],
        totalDiscountAmount: purchaseInfo.totalDiscountAmount + discountAmount,
      });

      // Reset the order counter
      setOrderCounter(0);
      alert('Order placed successfully with a 10% discount!');
    } else {
      alert('Invalid discount code');
    }
  };

  const generateDiscountCode = () => {
    const newDiscountCode = generateRandomDiscountCode();
    setDiscountCodes([...discountCodes, newDiscountCode]);
  };

  const calculateDiscount = (cartItems) => {
    const totalCartPrice = cartItems.reduce((total, item) => total + item.price, 0);
    const discountPercentage = 0.10; // 10%
    const discountAmount = totalCartPrice * discountPercentage;
    return discountAmount;
  };

  const generateRandomDiscountCode = () => {
    const codeLength = 8;
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let discountCode = '';

    for (let i = 0; i < codeLength; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      discountCode += characters[randomIndex];
    }

    return discountCode;
  };

  return (
    <div className="App">
      <div className="header">
        <h1>E-commerce Store</h1>
      </div>
      <div className="content">
        <div className="cart-section">

          <Cart cartItems={cartItems} addItemToCart={addItemToCart} />
        </div>
        <div className="checkout-section">

          <Checkout
            cartItems={cartItems}
            applyDiscountCode={applyDiscountCode}
            handleCheckout={handleCheckout}
          />
        </div>
      </div>
      <div className="admin-section">

        <Admin
          generateDiscountCode={generateDiscountCode}
          purchaseInfo={purchaseInfo}
        />
      </div>
    </div>
  );
}

export default App;
