import React from 'react';

function Admin({ generateDiscountCode, purchaseInfo }) {
  const calculateTotalDiscountAmount = (discountCodes) => {
    // Calculate the total discount amount based on discountCodes
    const totalDiscountAmount = discountCodes.reduce((total, code) => {
      // You may need to implement a logic to calculate the discount amount for each code
      return total + calculateDiscountAmountForCode(code);
    }, 0);

    return totalDiscountAmount;
  };

  const calculateDiscountAmountForCode = (code) => {
    // Implement logic to calculate the discount amount for a specific code
    // This logic depends on how discounts are applied
    // For example, if each code represents a fixed discount amount, you can use a lookup
    const discountAmounts = {
      DISCOUNT10: 10, // Change this as needed
      // Add more discount codes and their corresponding amounts as needed
    };

    return discountAmounts[code] || 0;
  };

  const totalDiscountAmount = calculateTotalDiscountAmount(purchaseInfo.discountCodes);

  return (
    <div>
      <h2>Admin</h2>
      <button onClick={generateDiscountCode}>Generate Discount Code</button>
      <div>
        <h3>Purchase Information</h3>
        <p>Total Items Purchased: {purchaseInfo.itemsCount}</p>
        <p>Total Purchase Amount: ${purchaseInfo.totalPurchaseAmount}</p>
        <p>Discount Codes: {purchaseInfo.discountCodes.join(', ')}</p>
        <p>Total Discount Amount: ${totalDiscountAmount.toFixed(2)}</p>
      </div>
    </div>
  );
}

export default Admin;
